/**
 *  PEPPER REDUX STORE - REDUCER
 * 
 */
import { session, getToken } from '../../util/okta';

const initialState = {
    user: null,
    provisional: null,
    token: null,
    sessionExists: null

};

function rootReducer(state = initialState, action) {
    if (Object.keys(reducers).includes(action.type)) {
        return reducers[action.type](state, action.payload);
    } else {
        return state;
    }
};

let reducers = {};

reducers.OKTA_SESSION = async (state) => {
    // Check for existing user session
    async function status() {
        let userSession = await session();
        let tokenInfo = await getToken(userSession);
        console.log(userSession, tokenInfo);
        return tokenInfo;
    }

    return await status()
        .then(sessionInfo => {
            if (sessionInfo.provisional) {
                return { ...state, provisional: sessionInfo.provisional }
            } else {
                return { ...state, token: sessionInfo.token, user: sessionInfo.user }
            }
        })

    

