import React, { Component } from "react";
import { Link } from "react-router-dom";

class MyGroups extends Component {
  state = {
    editGroup: false
  };

  changeState = () => {
    setTimeout(() => {
      this.setState(prevState => {
        let groups = [...prevState.groups];
        groups.forEach((g, index) => {
          groups[index].members = 10;
        });
        return { groups: groups };
      });
    }, 2000);
  };

  goToGroups = groupId => {
    if (groupId) {
      window.location.replace(`/foodcommune/group-page.html?ref=${groupId}`);
    } else {
      window.location.replace("/foodcommune/groups.html");
    }
  };

  editGroupList = () => {
    this.setState({ editGroup: this.state.editGroup ? false : true });
  };

  removeGroup = (ev, id) => {
    ev.stopPropagation();
    this.props.leaveGroup(id);
  };

 


  render() {
    return (
      <div className="card bg-light" style={{ width: "inherit" }}>
        <div className="card-header button-group">
          <div>Your Groups</div>
          <i
            onClick={this.editGroupList}
            style={{ fontSize: "medium", cursor: "pointer" }}
            className="fas fa-pencil-alt"
          ></i>
          <div>
            <Link
              to="/foodcommune/groups"
              className="btn btn-sm btn-primary mr-1"
              style={{ font: "small-caption", textDecoration: "none" }}
            >
              join a group
            </Link>
            <Link
              to={"/foodcommune/groups?option=1"}
              className="btn btn-sm btn-warning"
              style={{ font: "small-caption" }}
            >
              create group
            </Link>
          </div>
        </div>
        <div className="card-body p-0">
          <div className="container-fluid" id="group-list">
            <ul
              id="group-list"
              style={{ listStyle: "none", padding: "0", fontSize: "small" }}
            >
              {this.props.data &&
                this.props.data.MyClans.map((g, index) => {
                  return (
                    <li
                      key={index}
                      className="row"
                      style={{
                        backgroundColor:
                          index % 2 === 0 ? "dodgerblue" : "#adb5bd"
                      }}
                    >
                      <Link
                        to={"/foodcommune/grouppage?ref=" + g.id}
                        className="col group-item d-flex"
                        style={{
                          font: "small-caption",
                          textDecoration: "none"
                        }}
                      >
                        <div className="col-2">
                          {!g.image_url && (
                            <i className="fas fa-users group-img"></i>
                          )}
                          {g.image_url && (
                            <div className="img-wrap-sm">
                              <img
                                style={{ width: "inherit" }}
                                src={`./Uploads/Group_Images/${g.image_url}`}
                                alt=""
                              ></img>
                            </div>
                          )}
                        </div>
                        <div className="col-6">{g.name}</div>
                      </Link>
                      {this.state.editGroup && (
                        <div
                          style={{
                            padding: "5px",
                            display: "flex",
                            alignItems: "center",
                            backgroundColor: "whitesmoke"
                          }}
                        >
                          <button
                            style={{ fontSize: "x-small" }}
                            className="btn btn-sm btn-danger"
                            onClick={ev => this.removeGroup(ev, g.id)}
                          >
                            remove
                          </button>
                        </div>
                      )}
                    </li>
                  );
                })}
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default MyGroups;
