import React, { Component } from "react";
import { connect } from "react-redux";
import { oktaSession } from "../store/actions/index";
import { session, getToken } from "../util/okta";
import { Link, Redirect, Route } from "react-router-dom";
import DashboardView from "./DashboardView";

const mapStateToProps = state => {
  return {
    sessionExists: state.sessionExists,
    provisional: state.provisional,
    user: state.user,
    tokenExpiresAt: state.tokenExpiresAt,
    expiringSession: state.expiringSession
  };
};

function mapDispatchToProps(dispatch) {
  return {};
}

class UserStatus extends Component {
  state = {
    userMenu: false
  };

  componentWillMount() {
    session();
  }

  userMenu = () => {
    console.log("clicked");
    this.setState({ userMenu: this.state.userMenu ? false : true });
  };

  render() {
    return (
      <React.Fragment>
        {/* WARNING - SESSION EXPIRING */}
        {!this.props.expiringSession &&
          <div className="session-exp-warning">
          <span>Your session is about to expire!</span>
          <button className="btn btn-sm btn-danger">Log Out</button>
          <button className="btn btn-sm btn-success">Continue</button>
        </div>}
        {/* REDIRECT SIGNED IN USER */}
        {this.props.user && (
          <Route>
            <Redirect to={{ pathname: "/foodcommune/dashboard" }}></Redirect>
          </Route>
        )}
        {/* User sign in/create account */}
        {!this.props.user && (
          <React.Fragment>
            <span
              id="open-account"
              className="nav-item nav-link"
              onClick={this.props.createAccount}
            >
              Create Account
            </span>
            <span id="open-signin" className="nav-item nav-link">
              <Link to="/foodcommune/SignIn" className="link-gray">
                Log In
              </Link>
            </span>
          </React.Fragment>
        )}
        {/* User signed in */}
        {this.props.user && (
          <div>
            <div
              onClick={this.userMenu}
              style={{
                display: "flex",
                alignItems: "center",
                position: "relative"
              }}
            >
              {this.props.user.image_url && (
                <div className="img-wrap-xs mr-1">
                  <img
                    src={`./Uploads/Profile_Images/${this.props.user.image_url}`}
                    style={{ width: "inherit" }}
                  ></img>
                </div>
              )}
              {!this.props.user.image_url && (
                <i className="fas fa-user-circle fa-2x mr-1 fc-green"></i>
              )}
              <span id="user" style={{ color: "dimgray" }}>
                {this.props.user.first_name}
              </span>
              <i className="fas fa-caret-down ml-3"></i>
              {this.state.userMenu && (
                <div id="user-menu">
                  <ul style={{ listStyle: "none", padding: "10px" }}>
                    <li
                      style={{ cursor: "pointer" }}
                      onClick={() => this.state.authClient.signOut()}
                    >
                      Sign Out
                    </li>
                    <li style={{ cursor: "pointer" }}>
                      <Link to="/foodcommune/dashboard">Dashboard</Link>
                    </li>
                    <li
                      style={{ cursor: "pointer" }}
                      onClick={this.tokenExpireCheck}
                    >
                      Settings
                    </li>
                  </ul>
                </div>
              )}
            </div>
          </div>
        )}
      </React.Fragment>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(UserStatus);
