import React, { Component } from "react";
import MyGroupOrders from "./MyGroupOrders";
import { Link } from "react-router-dom";

class MyGroupBuys extends Component {
  state = {
    menuIndex: null,
    viewOrder: null,
    showBuyOptions: null,
    buyQuantity: 0
  };

  totalUnitsPurchased = dealId => {
    let units = 0;
    this.props.data.Purchases.forEach(p => {
      if (p.deal_id === dealId) {
        units += p.purchase_units;
      }
    });

    return units;
  };

  seeOrder = order => {
    this.setState({ viewOrder: order });
  };

  render() {
    return (
      <React.Fragment>
        {this.state.viewOrder && (
          <MyGroupOrders
            data={this.props.data}
            Buy={this.state.viewOrder}
            seeOrder={this.seeOrder}
          />
        )}
        {!this.state.viewOrder && (
          <div className="card bg-light">
            <div className="card-header button-group">
              <div>Our Buys - Place Your Order Now!</div>
              <Link
                to="/foodcommune/catalog"
                className="btn btn-sm btn-primary"
                style={{ font: "small-caption", float: "right" }}
              >
                catalog
              </Link>
            </div>
            <div className="card-body p-0" style={{ overflowY: "auto" }}>
              <div className="container-fluid" id="group-list">
                <ul
                  id="group-buys-list"
                  style={{ listStyle: "none", padding: "0", fontSize: "small" }}
                >
                  {this.props.data &&
                    this.props.data.Buys.map((b, index) => {
                      return (
                        <React.Fragment>
                          <li
                            key={index}
                            className="row group-buy-item"
                            style={{
                              boxShadow:
                                this.state.showBuyOptions === index
                                  ? "0px 4px 6px 1px rgba(0, 0, 0, 0.2)"
                                  : "none"
                            }}
                            onClick={() =>
                              this.setState({
                                showBuyOptions:
                                  this.state.showBuyOptions === index
                                    ? null
                                    : index
                              })
                            }
                          >
                            {/* Item image */}
                            <div className="col-2">
                              <img
                                className="seller-img"
                                src={"./Uploads/Deal_Images/" + b.image_url}
                                alt=""
                              />
                            </div>
                            {/* Item info */}
                            <div
                              className="col-10"
                              style={{ color: "dimgray" }}
                            >
                              <span className="item-emph">{b.description}</span>
                            </div>

                            {this.props.userIsMember == 0 && (
                              <div
                                style={{ position: "absolute", right: "5px" }}
                                className="buy-ellips d-flex"
                                onClick={ev => {
                                  ev.stopPropagation();
                                  this.setState({
                                    menuIndex:
                                      index === this.state.menuIndex
                                        ? null
                                        : index
                                  });
                                }}
                              >
                                <i className="fas fa-ellipsis-v"></i>
                                {this.state.menuIndex === index && (
                                  <div className="buy-ellips-menu">
                                    <div
                                      onClick={() => this.props.removeBuy(b.id)}
                                    >
                                      Remove
                                    </div>
                                    <div
                                      className="mt-1"
                                      onClick={() => this.seeOrder(b)}
                                    >
                                      Orders
                                    </div>
                                  </div>
                                )}
                              </div>
                            )}
                          </li>
                          <div
                            className={
                              index === this.state.showBuyOptions
                                ? "buy-options bo-show"
                                : "buy-options"
                            }
                          >
                            <div className="row justify-content-center align-items-center my-2">
                              <div
                                className="col-4"
                                style={{ justifyContent: "space-between" }}
                              >
                                <div>
                                  <span className="buy-cost">
                                    Cost $
                                    {(
                                      (b.price / b.units) *
                                      this.state.buyQuantity
                                    ).toFixed(2)}
                                  </span>
                                </div>
                              </div>
                              <div className="col-5 text-center">
                                <span>Order in {b.unit_measure}</span>
                                <input
                                  className="ml-2"
                                  style={{
                                    borderStyle: "none",
                                    borderRadius: "3px",
                                    width: "50px",
                                    textAlign: "center"
                                  }}
                                  value={this.state.buyQuantity}
                                  type="number"
                                  onChange={ev =>
                                    this.setState({
                                      buyQuantity: ev.target.value
                                    })
                                  }
                                ></input>
                              </div>
                              <div className="col-3">
                                {" "}
                                <div>
                                  <button
                                    style={{fontSize:"x-small"}}
                                    className="btn btn-sm btn-primary mx-2 mb-1 mb-md-0"
                                    onClick={() =>
                                      this.setState({
                                        buyQuantity: this.state.buyQuantity + 1
                                      })
                                    }
                                  >
                                    <i className="fas fa-plus"></i>
                                  </button>
                                  <button
                                    style={{fontSize:"x-small"}}
                                    className="btn btn-sm btn-primary mx-2"
                                    onClick={() =>
                                      this.setState({
                                        buyQuantity: this.state.buyQuantity - 1
                                      })
                                    }
                                  >
                                    <i className="fas fa-minus"></i>
                                  </button>
                                </div>
                              </div>
                            </div>
                            <div className="row justify-content-center align-items-center">
                              <div
                                className="col-7"
                                style={{
                                  fontSize: "x-small",
                                  fontWeight: "700"
                                }}
                              >
                                <div>Description: {b.summary}</div>
                                <div>
                                  Deal: {b.units} {b.unit_measure} for $
                                  {b.price}
                                </div>
                              </div>
                              <div
                                className="col-3"
                                style={{
                                  fontSize: "x-small",
                                  fontWeight: "700"
                                }}
                              >
                                <div>
                                  Group orders {this.totalUnitsPurchased(b.id)}/
                                  {b.units}
                                  {b.unit_measure}
                                </div>
                              </div>
                              <div className="col-12 col-md-2 text-center">
                                <button className="btn btn-sm btn-success">
                                  Buy
                                </button>
                              </div>
                            </div>
                          </div>
                        </React.Fragment>
                      );
                    })}
                </ul>
              </div>
            </div>
          </div>
        )}
      </React.Fragment>
    );
  }
}

export default MyGroupBuys;
