/**
 * File:        okta.js
 * 
 * Description: 
 */
import store from '../store/js/index';
import { setSession } from '../store/actions/index';
// import OktaAuth from '@okta/okta-auth-js';
let OktaAuth = require('@okta/okta-auth-js');



console.log("running auth client..");
//  Okta browser SDK AuthClient
var config = {
    issuer: "https://dev-236245.okta.com/oauth2/default",
    clientId: "0oa4lqivbsKPbAtfs4x6",
    redirectUri: "https://qumoc.com/foodcommune",
    responseMode: "fragment",
    tokenManager: {
        storage: "sessionStorage"
    },
    onSessionExpired: function () {
        console.log("re-authorization is required");
        // authClient.getWithRedirect();
    }
};

// AUTH CLIENT
let authClient = new OktaAuth(config);


export const session = async () => {
    let options = {
        method: "POST",
        mode: "same-origin",
        credentials: "same-origin",
        headers: {
            "Content-Type": "application/json"
        }
    };

    fetch("/foodcommune/site/usersession", options)
        .then(res => {
            console.log(res)
        })
        .catch(err => {
            console.log(err);
        })

    return sessionExists;
}

export const getToken = async (session) => {
    console.log("GETTOKEN")
    // Check for user session
    if (session) {

        // Request token for API access
        let options = {
            method: "POST",
            mode: "same-origin",
            credentials: "same-origin",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({ entity: { login: session.user.profile.login, request: "test" } })
        };

        fetch("/foodcommune/secure", options)
            .then(res => {
                if (res) return res.json();
            })
            .then(res => {
                if (res.success) {
                    store.dispatch(setSession({ token: res.token, user: res.user }));

                    // Set secure cookies
                    // document.cookie = `fc-user-auth = ${res.token};expires=`
                }
            })
            .catch(err => {
                console.log(err);
            });
    } else {

        // Get provisional site access
        let options = {
            method: "POST",
            mode: "same-origin",
            credentials: "same-origin",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({})
        };
        fetch("/foodcommune/secure/provisional", options)
            .then(res => {
                return res.json();
            })
            .then(res => {
                store.dispatch(setSession({ provisional: res.token }));
            })
            .catch(err => {
                console.log(err)
                store.dispatch(setSession({ provisional: "test token" }));
            })
    }
};

export const signInAuthenticate = async (email, password) => {
    let transaction = await authClient
        .signIn({
            username: email,
            password: password
        })
        .catch(err => {
            // this.setState({ errMsg: "Email or password is incorrect!" });
            console.log(err);
        })
        .then(res => {
            console.log(res);
            if (res.status === 'SUCCESS') {
                getToken(res);


            }
        })

    // if (transaction && transaction.status === "SUCCESS") {
    //   console.log("SUCCESS", transaction);
    //   authClient.session.setCookieAndRedirect(
    //     transaction.sessionToken,
    //     "/foodcommune/dashboard"
    //   ); // Sets a cookie on redirect
    // }
}
