import React, { Component } from 'react';
import MyGroupOrders from './MyGroupOrders';

class MyGroupBuys extends Component {
  state = {
    menuIndex: null,
    viewOrder: null
  };

  totalUnitsPurchased = dealId => {
    let units = 0;
    this.props.data.Purchases.forEach(p => {
      if (p.deal_id === dealId) {
        units += p.purchase_units;
      }
    });

    return units;
  };

  seeOrder = order => {
    this.setState({ viewOrder: order });
  };

  render() {
    return (
      <React.Fragment>
        {this.state.viewOrder && (
          <MyGroupOrders
            data={this.props.data}
            Buy={this.state.viewOrder}
            seeOrder={this.seeOrder}
          />
        )}
        {!this.state.viewOrder && (
          <div className="card bg-light">
            <div className="card-header button-group">
              <div>Our Buys - Place Your Order Now!</div>
              <button
                className="btn btn-sm btn-primary"
                style={{ font: "small-caption", float: "right" }}
                onClick={() => {
                  window.location.href = "/foodcommune/catalog.html";
                }}
              >
                catalog
              </button>
            </div>
            <div className="card-body p-0" style={{ overflowY: "auto" }}>
              <div className="container-fluid" id="group-list">
                <ul
                  id="group-buys-list"
                  style={{ listStyle: "none", padding: "0", fontSize: "small" }}
                >
                  {this.props.data &&
                    this.props.data.Buys.map((b, index) => {
                      return (
                        <li
                          key={index}
                          className="row group-buy-item"
                          onClick={() => this.props.itemView(b)}
                        >
                          {/* Item image */}
                          <div className="col-2">
                            <img
                              className="seller-img"
                              src={"./Uploads/Deal_Images/" + b.image_url}
                              alt=""
                            />
                          </div>
                          {/* Item info */}
                          <div className="col-6" style={{ color: "dimgray" }}>
                            <span className="item-emph">{b.description}</span>
                          </div>
                          <div className="col-3" style={{ color: "#28a745" }}>
                            {this.totalUnitsPurchased(b.id)}/{b.units}
                            {b.unit_measure}
                          </div>
                          {this.props.userIsMember == 0 && (
                            <div
                              className="col-1 buy-ellips d-flex"
                              onClick={ev => {
                                ev.stopPropagation();
                                this.setState({
                                  menuIndex:
                                    index === this.state.menuIndex
                                      ? null
                                      : index
                                });
                              }}
                            >
                              <i className="fas fa-ellipsis-v"></i>
                              {this.state.menuIndex === index && (
                                <div className="buy-ellips-menu">
                                  <div
                                    onClick={() => this.props.removeBuy(b.id)}
                                  >
                                    Remove
                                  </div>
                                  <div
                                    className="mt-1"
                                    onClick={() => this.seeOrder(b)}
                                  >
                                    Orders
                                  </div>
                                </div>
                              )}
                            </div>
                          )}
                        </li>
                      );
                    })}
                </ul>
              </div>
            </div>
          </div>
        )}
      </React.Fragment>
    );
  }
}

export default MyGroupBuys;
