import React, { Component } from "react";
import SearchBar from "./SearchBar";
import GroupList from "./GroupList";
import CreateGroup from "./CreateGroup";
import { connect } from "react-redux";

const mapStateToProps = state => {
  return {
    provisional: state.provisional,
    user: state.user,
    token: state.token
  };
};

class GroupsView extends Component {
  state = {
    searchInput: "",
    showAddGroup: false
  };

  componentDidMount() {
    let params = new URLSearchParams(window.location.search);
    let option = params.get("option");

    if (option) this.setState({ showAddGroup: true });
  }

  componentWillMount() {
    fetchData(this.props.token, this.props.user.id)
      .then(resolved => {
        console.log(resolved);
        this.setState({ data: resolved });
      })
      .catch(err => {
        console.log(err);
      });
  }

  onSearch = ev => {
    let value = ev.target.value;

    this.setState({ searchInput: value });
  };

  addGroup = toggle => {
    this.setState({ showAddGroup: this.state.showAddGroup ? false : true });
  };

  newGroup = payload => {
    payload.user_id = this.props.user.id;

    insertGroup(this.props.token, payload)
      .then(resolved => {
        console.log(resolved);
        // this.setState((prevState) => {
        //     let Deals = [...prevState.data.Deals];
        //     let deal = Deals.findIndex(obj => { return obj.id === payload.id });
        //     Deals[deal] = payload;

        //     let data = { ...prevState.data, Deals: Deals }
        //     return { data: data };
        // }, () => {
        //         this.setState({ showAddDeal: false });
        // })
      })
      .catch(err => {
        console.log(err);
      });
  };

  render() {
    return (
      <React.Fragment>
        {/* <!-- Main Content --> */}
        <div className="row" style={{ backgroundColor: "white" }}>
          {this.state.showAddGroup && (
            <CreateGroup newGroup={this.newGroup} addGroup={this.addGroup} />
          )}
          <div id="group-view" className="col-12">
            <SearchBar
              onSearch={this.onSearch}
              placeHolderVal={"Search for group or area..."}
            />
            <GroupList
              searchValue={this.state.searchInput}
              data={this.state.data}
              addGroup={this.addGroup}
            />
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default connect(mapStateToProps)(GroupsView);

function fetchData(token, userid) {
  console.log("fetching data......", token, userid);
  return new Promise((resolve, reject) => {
    let request = { userId: userid };
    let options = {
      method: "POST",
      headers: new Headers({
        Authorization: "Bearer " + token,
        "Content-Type": "application/json"
      }),
      body: JSON.stringify({
        entity: request
      })
    };
    fetch("/foodcommune/accounts/groups", options)
      .then(res => {
        return res.json();
      })
      .then(res => {
        if (!res.success) {
          // Return to account detail and display error message
          resolve(res);
        } else {
          reject("Unable to retrieve data");
        }
      });
  });
}

function insertGroup(token, payload) {
  console.log("fetching data......");
  return new Promise((resolve, reject) => {
    let options = {
      method: "POST",
      headers: new Headers({
        Authorization: "Bearer " + token,
        "Content-Type": "application/json"
      }),
      body: JSON.stringify({
        entity: payload
      })
    };
    fetch("/foodcommune/accounts/createGroup", options)
      .then(res => {
        return res.json();
      })
      .then(res => {
        console.log(res);
        if (res.success) {
          // Return to account detail and display error message
          resolve(res);
        } else {
          reject("Unable to retrieve data");
        }
      });
  });
}
