import React, { Component } from "react";
import { connect } from "react-redux";
import { oktaSession } from "../store/actions/index";

function mapDispatchToProps(dispatch) {
  return {
    oktaSession: () => dispatch(oktaSession())
  };
}

class UserStatus extends Component {
  state = {};
  constructor(props) {
    super(props);
    this.state = {
      provisional: null,
      userMenu: false, // Toggle for user meny
      // authClient: this.newAuthClient(),
      session: false,
      provisional: null,
      username: ""
    };
  }

  componentDidMount() {
    this.props.oktaSession();
  }

  userMenu = () => {
    console.log("clicked");
    this.setState({ userMenu: this.state.userMenu ? false : true });
  };


  render() {
    return (
      <React.Fragment>
        {/* User sign in/create account */}
        {!this.state.session && (
          <React.Fragment>
            <span
              id="open-account"
              className="nav-item nav-link"
              onClick={this.props.createAccount}
            >
              Create Account
            </span>
            <span
              id="open-signin"
              className="nav-item nav-link"
              onClick={this.props.signIn}
            >
              Sign In
            </span>
          </React.Fragment>
        )}

        {/* User signed in */}
        {this.state.session && (
          <div>
            <div
              onClick={this.userMenu}
              style={{
                display: "flex",
                alignItems: "center",
                position: "relative"
              }}
            >
              <i className="fas fa-user-circle fa-2x mr-1 fc-green"></i>
              <span id="user" style={{ color: "dimgray" }}>
                {this.state.username}
              </span>
              <i className="fas fa-caret-down ml-3"></i>
              {this.state.userMenu && (
                <div id="user-menu">
                  <ul style={{ listStyle: "none", padding: "10px" }}>
                    <li
                      style={{ cursor: "pointer" }}
                      onClick={() => this.state.authClient.signOut()}
                    >
                      Sign Out
                    </li>
                    <li
                      style={{ cursor: "pointer" }}
                      onClick={() =>
                        (window.location.href = "/foodcommune/mydashboard.html")
                      }
                    >
                      Dashboard
                    </li>
                    <li style={{ cursor: "pointer" }}>Settings</li>
                  </ul>
                </div>
              )}
            </div>
          </div>
        )}
      </React.Fragment>
    );
  }
}

export default connect(null, mapDispatchToProps)(UserStatus);
