import React, { Component } from 'react';

class SignIn extends Component {
  state = {
    email: "",
    password: "",
    errMsg: ""
  };

  closeSignInWindow = ev => {
    if (ev.target.id === "sign-in-wrapper") this.props.signIn();
  };

  inputEmail = ev => {
    this.setState({ email: ev.target.value });
  };

  inputPassword = ev => {
    this.setState({ password: ev.target.value });
  };

  // signInAuthenticate = async () => {
  //   let authClient = this.props.authClient;

  //   let transaction = await authClient
  //     .signIn({
  //       username: this.state.email,
  //       password: this.state.password
  //     })
  //     .catch(err => {
  //       this.setState({ errMsg: "Email or password is incorrect!" });
  //       console.log(err);
  //     });

  //   if (transaction && transaction.status === "SUCCESS") {
  //     console.log("SUCCESS", transaction);
  //     authClient.session.setCookieAndRedirect(
  //       transaction.sessionToken,
  //       "https://qumoc.com/foodcommune/mydashboard.html"
  //     ); // Sets a cookie on redirect
  //   }
  // };

  render() {
    return (
      // <!-- Sign In  -->

      <React.Fragment>
        <div
          id="sign-in-wrapper"
          className={this.props.signInToggle ? "show-sign-in" : ""}
          onClick={ev => this.closeSignInWindow(ev)}
        >
          <div id="sign-in">
            <div className="header spartan">
              <img width="40px" src={require("../media/logoAlpha.gif")} alt="" />
              <div>Sign In</div>
            </div>
            <div className="content spartan">
              {/* <!-- email --> */}
              <div className="form-group">
                <label>Email</label>
                <input
                  onChange={ev => this.inputEmail(ev)}
                  type="email"
                  className="form-control"
                  id="sign-in-email"
                ></input>
              </div>
              {/* <!-- password --> */}
              <div className="form-group">
                <label>Password</label>
                <input
                  onChange={ev => this.inputPassword(ev)}
                  type="password"
                  className="form-control"
                  id="sign-in-password"
                ></input>
              </div>
              <button
                id="signin-go"
                className="btn btn-primary"
                onClick={this.signInAuthenticate}
              >
                Sign-In
              </button>

              <div
                id="sign-in-form-error"
                className="spartan pt-2"
                style={{
                  fontSize: "x-small",
                  color: "red",
                  textAlign: "center"
                }}
              >
                {this.state.errMsg}
              </div>
              <div id="password-reset" className="py-1">
                <small
                  onClick={() => {
                    window.location.href =
                      "/foodcommune/account-login-help.html";
                  }}
                  style={{ fontWeight: "500", cursor: "pointer" }}
                >
                  Reset Password
                </small>
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default SignIn;
