import React, { Component } from 'react';

class UserStatus extends Component {
  state = {};
  constructor(props) {
    super(props);
    this.state = {
      provisional: null,
      userMenu: false, // Toggle for user meny
      authClient: this.newAuthClient(),
      session: false,
      provisional: null,
      username: ""
    };
  }

  componentDidMount() {
    this.session();
  }

  userMenu = () => {
    console.log("clicked");
    this.setState({ userMenu: this.state.userMenu ? false : true });
  };

  newAuthClient = () => {
    console.log("running auth client..");
    //  Okta browser SDK AuthClient
    var config = {
      issuer: "https://dev-236245.okta.com/oauth2/default",
      clientId: "0oa4lqivbsKPbAtfs4x6",
      redirectUri: "https://qumoc.com/foodcommune",
      responseMode: "fragment",
      tokenManager: {
        storage: "sessionStorage"
      },
      onSessionExpired: function() {
        console.log("re-authorization is required");
        // authClient.getWithRedirect();
      }
    };

    // AUTH CLIENT
    let auth = new OktaAuth(config);
    this.props.updateParentState({ authClient: auth });
    return auth;
  };

  session = async () => {
    let authClient = this.state.authClient;
    let session = await authClient.session.exists(); //.then(function(exists) {

    // Check for user session
    if (session) {
      this.setState({ session: true });
      let user = await authClient.session.get();
      $("#user").html(user.login);

      // Request token for API access
      let options = {
        method: "POST",
        mode: "same-origin",
        credentials: "same-origin",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({ entity: { login: user.login, request: "test" } })
      };

      fetch("/foodcommune/secure", options)
        .then(res => {
          return res.json();
        })
          .then(res => {
          if (res.success) {
            if (this.props.setToken) this.props.setToken(res.token);
            if (this.props.setUser) this.props.setUser(res.user);
            this.setState({ username: res.user.first_name });
          }
        })
        .then(() => {
          if (this.props.viewData) this.props.viewData();
        });
    } else {
    //     this.setState({ session: false });
    //     console.log(window.location.href)
    //   If signed out and not on home page redirect
    //   if (window.location.href !== "/foodcommune/") {
        // setTimeout(function() {
        //   window.location.replace("/foodcommune");
        // }, 1);
    //   }

      // Get provisional site access
      let options = {
        method: "POST",
        mode: "same-origin",
        credentials: "same-origin",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({ access: this.state.provisional })
      };
      fetch("/foodcommune/secure/provisional", options)
        .then(res => {
          return res.json();
        })
        .then(res => {
          this.setState({ provisional: res.token });
          this.props.updateParentState({ provisional: res.token });
        });
    }
  };

  render() {
    return (
      <React.Fragment>
        {/* User sign in/create account */}
        {!this.state.session && (
          <React.Fragment>
            <span
              id="open-account"
              className="nav-item nav-link"
              onClick={this.props.createAccount}
            >
              Create Account
            </span>
            <span
              id="open-signin"
              className="nav-item nav-link"
              onClick={this.props.signIn}
            >
              Sign In
            </span>
          </React.Fragment>
        )}

        {/* User signed in */}
        {this.state.session && (
          <div>
            <div
              onClick={this.userMenu}
              style={{
                display: "flex",
                alignItems: "center",
                position: "relative"
              }}
            >
              <i className="fas fa-user-circle fa-2x mr-1 fc-green"></i>
              <span id="user" style={{ color: "dimgray" }}>
                {this.state.username}
              </span>
              <i className="fas fa-caret-down ml-3"></i>
              {this.state.userMenu && (
                <div id="user-menu">
                  <ul style={{ listStyle: "none", padding: "10px" }}>
                    <li
                      style={{ cursor: "pointer" }}
                      onClick={() => this.state.authClient.signOut()}
                    >
                      Sign Out
                    </li>
                    <li style={{ cursor: "pointer" }} onClick={()=> window.location.href='/foodcommune/mydashboard.html'}>Dashboard</li>
                    <li style={{ cursor: "pointer" }}>Settings</li>
                  </ul>
                </div>
              )}
            </div>
          </div>
        )}
        {/* <!-- OKTA SDK --> */}
    <script src="./js/okta-auth-js.min.js"></script>
      </React.Fragment>
    );
  }
}

export default UserStatus;
