import React, { Component } from 'react';


class AddDeal extends Component {
  constructor(props) {
    super(props);
    if (props.editDeal) {
        this.state = props.editDeal;
        console.log(props.editDeal)
    }
  }
  state = {
    description: "",
    summary: "",
    category: "",
    unit_measure: "",
    units: "",
      price: "",
  };

  onFormChange = ev => {
    let set = {};
    set[ev.target.id] = ev.target.value;
    this.setState(set);
  };

  createDeal = () => {
    let payload = {};
    Object.keys(this.state).forEach(k => {
      payload[k] = this.state[k];
    });

    let file = document.getElementById("image_url").files[0];
    let formData = new FormData();
    formData.append("file", file);

    // Append file name and user id to payload
    if (file) {
      payload.image_url = `${this.props.user.id}@${file.name}`;
      payload.user_id = this.props.user.id;
      console.log(payload);

      // Send image
      fetch(`/foodcommune/imageUpload?userid=${this.props.user.id}`, {
        body: formData,
        method: "POST"
      })
        .then(res => {
          return res.json();
        })
        .then(res => {
          console.log(res);
        });
    }

    if (this.props.editDeal) {
      // Update Deal
      console.log(payload);
        this.props.updateDeal(payload);
    } else {
      // Insert Deal
      this.props.newDeal(payload);
    }
  };

  render() {
    return (
      <div
        id="item-view-container"
        onClick={ev => {
          if (ev.target.id === "item-view-container") this.props.showAddDeal();
        }}
        className="view-container"
        style={{
          overflowY: "auto",
          position: "absolute",
          height: "100%",
          width: "100%",
          zIndex: "1000",
          top: "0"
        }}
      >
        <div
          className="modal-dialog"
          role="document"
          style={{
            boxShadow: "0 0 16px 2px rgba(0,0,0,.3)",
            maxWidth: "425pm!important"
          }}
        >
          <div className="modal-content">
            <div className="modal-close" onClick={this.props.showAddDeal}>
            <i class="fas fa-window-close modal-close"></i>
            </div>
            <div
              className="header spartan d-flex align-items-center justify-content-center"
              style={{
                backgroundColor: "#346802",
                color: "white",
                padding: "12px"
              }}
            >
              <img width="40px" src="./media/logoAlpha.gif" alt="" />{" "}
              <div>{this.props.editDeal ? "Editing Deal" : "New Deal"}</div>
            </div>
            <div className="modal-body">
              {/* Description */}
              <div className="form-group">
                <label>Description</label>
                <input
                  onChange={ev => this.onFormChange(ev)}
                  value={this.state.description}
                  type="text"
                  className="form-control"
                  id="description"
                ></input>
                <small className="text-muted">
                  maximum length 30 characters
                </small>
              </div>
              {/* Summary  */}
              <div className="form-group">
                <label>Summary - Tell them about your deal</label>
                <textarea
                  onChange={ev => this.onFormChange(ev)}
                  value={this.state.summary}
                  type="text"
                  className="form-control"
                  id="summary"
                ></textarea>
                <small className="text-muted">
                  maximum length 250 characters
                </small>
              </div>

              <div className="row">
                <div className="col">
                  {/* Category */}
                  <div className="form-group">
                    <label>Category</label>
                    <select
                      onChange={ev => this.onFormChange(ev)}
                      value={this.state.category}
                      id="category"
                      className="form-control form-control-sm"
                    >
                      <option>Fruit</option>
                      <option>Vegetables</option>
                      <option>Spices and Stuff</option>
                      <option>Chesse and Dairy</option>
                      <option>Meats and Poultry</option>
                      <option>Seafood</option>
                    </select>
                  </div>
                </div>
                <div className="col">
                  {/* Unit Measure*/}
                  <div className="form-group">
                    <label>Unit measure</label>
                    <select
                      onChange={ev => this.onFormChange(ev)}
                      value={this.state.unit_measure}
                      id="unit_measure"
                      className="form-control form-control-sm"
                    >
                      <option value="lbs">lbs (pounds)</option>
                      <option value="oz">oz (ounces)</option>
                      <option value="kg">kg (kilograms)</option>
                      <option value="g">g (grams)</option>
                      <option value="gal">gal (gallons)</option>
                      <option value="qt">qt (quarts)</option>
                      <option value="case">case</option>
                      <option value="doz">doz (dozen)</option>
                    </select>
                  </div>
                </div>
              </div>

              <div className="row">
                <div className="col">
                  {/* Price */}
                  <div className="form-group">
                    <label>Price</label>
                    <input
                      onChange={ev => this.onFormChange(ev)}
                      value={this.state.price}
                      type="number"
                      className="form-control"
                      id="price"
                    ></input>
                    <small className="text-muted">
                      No need to include a '$' symbol
                    </small>
                  </div>
                </div>
                <div className="col">
                  {/* Units */}
                  <div className="form-group">
                    <label>Units</label>
                    <input
                      onChange={ev => this.onFormChange(ev)}
                      value={this.state.units}
                      type="number"
                      className="form-control"
                      id="units"
                    ></input>
                    <small className="text-muted">
                      Number of units included in your item
                    </small>
                  </div>
                </div>
              </div>

              {/* Upload image */}
              <div className="form-group">
                <label>Upload an image of your product</label>
                <input
                  //   value={this.state.image_url}
                  type="file"
                  className="form-control-file"
                  id="image_url"
                ></input>
              </div>
            </div>
            <div
              className="modal-footer"
              style={{ justifyContent: "space-evenly" }}
            >
              {!this.props.editDeal && (
                <button
                  className="btn btn-sm btn-primary"
                  onClick={this.createDeal}
                >
                  Create
                </button>
              )}
              {this.props.editDeal && (
                <button
                  className="btn btn-sm btn-primary"
                  onClick={this.createDeal}
                >
                  Update
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default AddDeal;
