 * 
 */
import { session, getToken } from '../../util/okta';

const initialState = {
    user: null,
    provisional: null,
    token: null,
    sessionExists: null

};

function rootReducer(state = initialState, action) {
    if (Object.keys(reducers).includes(action.type)) {
        return reducers[action.type](state, action.payload);
    } else {
        return state;
    }
};

