import React, { Component } from "react";
import { Link } from "react-router-dom";

class Buys extends Component {
  state = {};

  render() {
    return (
      <React.Fragment>
        <div className="card" style={{ height: "auto", maxHeight: "initial" }}>
          <div className="card-header text-center bg-light">
            Your Buys
            <button
              className="btn btn-sm btn-primary"
              style={{ font: "small-caption", float: "right" }}
              onClick={() => {
                window.location.href = "/foodcommune/catalog.html";
              }}
            >
              catalog
            </button>
          </div>
          <div
            className="card-body py-2 px-2"
            style={{
              maxHeight: "450px",
              overflowY: "auto",
              overflowX: "hidden",
              backgroundColor:"#f5f5f5"
            }}
          >
            <ul
              id="buys-list"
              style={{
                fontSize: "x-small",
                listStyle: "none",
                paddingInlineStart: "0"
              }}
            >
              {/* BUY LIST */}
              {this.props.data &&
                this.props.data.Buys.map((buy, index) => {
                  return buyItem(buy, index);
                })}
              {!this.props.data && <div>No items to display</div>}
            </ul>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

function buyItem(item, index) {
  let stars = score => {
    let scoreArr = [];
    for (let s = 0; s < 5; s++) {
      if (s < score) {
        scoreArr.push(<i key={s} className="fas fa-star sY"></i>);
      } else {
        scoreArr.push(<i key={s} className="fas fa-star sG"></i>);
      }
    }
    return scoreArr;
  };

  return (
    <li
      key={index}
      className="item-list-wrap my-2"
      style={{ backgroundColor: index % 2 === 0 ? "white" : "white" }}
    >
      <Link
        className="row"
        style={{
          textDecoration: "none",
          color: "dimgray"
        }}
      >
        <div className="col-6 col-md-3 mb-2 mb-md-0">
          {item.image_url && (
            <img
              src={"/foodcommune/Uploads/Deal_Images/" + item.image_url}
              alt=""
              style={{ maxHeight: "100px", borderRadius: "5px" }}
            ></img>
          )}
          {!item.image_url && (
            <img
              src="/foodcommune/media/no_image.png"
              alt=""
              style={{ maxHeight: "100px" }}
            ></img>
          )}
        </div>
        <div className="col-6 col-md-3">
          <div className="item-description">{item.description}</div>
          <div className="item-info my-2">
            {item.units} {item.unit_measure} for ${item.price}
          </div>
          <div className="item-seller mt-3">
            <img
              className="seller-img"
              src={"/foodcommune/Uploads/Profile_Images/" + item.seller_image}
              alt=""
            ></img>
            <div className="seller-name">
              <div>{item.seller_name}</div>
              <div className="stars justify-content-center">
                {stars(item.score)}
              </div>
            </div>
          </div>
        </div>
        <div className="col-12 col-md-6">{item.summary}</div>
      </Link>
    </li>
    // <div key={index} className="catalog-item mx-2" style={{ width: "180px" }}>
    //   <div className="item-header">
    //     <img src={'/foodcommune/Uploads/Deal_Images/' + item.image_url} alt=""></img>
    //   </div>
    //   <div className="item-info-group">
    //     <div className="item-description">{item.description}</div>
    //     <div className="item-info">{item.units} {item.unit_measure} for ${item.price}</div>
    //     <div className="item-seller">
    //       <img className="seller-img" src={'/foodcommune/Uploads/Profile_Images/' + item.seller_image} alt=""></img>
    //       <div className="seller-name">
    //         <div>{item.seller}</div>
    //         <div className="stars justify-content-center">
    //           {stars(item.score)}
    //         </div>
    //       </div>
    //     </div>
    //   </div>
    // </div>
  );
}

export default Buys;
