import React from 'react';


function CatalogActions(token, userClans, userId, deal) {
  let state = {
    groupSelected: undefined
  };

  let addToGroup = () => {
    let request = {
      addType: state.groupSelected === undefined ? "mylist" : "clan",
      userId: userId,
      clanId: state.groupSelected,
      dealId: deal.id,
      sellerId: deal.user_id
    };
    let options = {
      method: "POST",
      headers: new Headers({
        Authorization: "Bearer " + token,
        "Content-Type": "application/json"
      }),
      body: JSON.stringify({
        entity: request
      })
    };
    fetch("/foodcommune/accounts/addDealTo", options)
      .then(res => {
        return res.json();
      })
      .then(res => {
        console.log(res);
        if (res.success) {
          // Return to account detail and display error message
          console.log(res);
        } else {
          console.log("Unable to retrieve data");
        }
      });
  };

  return (
    <React.Fragment>
      <div className="col-6">
        <button
          type="fc"
          className="btn btn-light mb-2"
          style={{ margin: "0 auto" }}
          onClick={addToGroup}
        >
          <div className="btn-fc"></div>Add To Group
        </button>
        <div className="form-group">
          <select
            onChange={ev => (state.groupSelected = ev.target.value)}
            value={state.groupSelected}
            id="category"
            className="form-control form-control-sm"
          >
            <option value="0">select a group</option>
            {userClans.map(c => {
              return (
                <option className="p-2" value={c.id}>
                  {c.name}
                </option>
              );
            })}
          </select>
        </div>
      </div>
      <div
        className="col-6"
        style={{
          display: "flex",
          alignSelf: "start",
          justifyContent: "center"
        }}
      >
        <button type="fc" className="btn btn-light" onClick={addToGroup}>
          <div className="btn-fc"></div>Add To Your List
        </button>
      </div>
    </React.Fragment>
  );
};


function GroupPageActions(token, userId, clanId, deal, update) {
  let state = {
    order_quantity: update ? update.purchase_units : 0
  };

  let placeOrder = () => {
    let request = {
      userId: userId,
      clanId: clanId,
      dealId: deal.id,
      sellerId: deal.user_id,
      purchaseUnits: state.order_quantity,
      unitPrice: deal.price,
      units: deal.units
    };
    let options = {
      method: "POST",
      headers: new Headers({
        Authorization: "Bearer " + token,
        "Content-Type": "application/json"
      }),
      body: JSON.stringify({
        entity: request
      })
    };
    fetch("/foodcommune/accounts/groupPurchase", options)
      .then(res => {
        return res.json();
      })
      .then(res => {
        console.log(res);
        if (res.success) {
          // Return to account detail and display error message
          console.log(res);
        } else {
          console.log("Unable to retrieve data");
        }
      });
  };

  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "space-evenly",
        width: "100%"
      }}
    >
      <div
        className="form-group"
        style={{
          display: "flex",
          justifyContent: "space-evenly",
          alignItems: "center",
          margin: "0"
        }}
      >
        <label style={{ marginRight: "5px" }}>Quantity in pounds</label>
        <input
          defaultValue={update ? update.purchase_units : 0}
          onChange={ev => (state.order_quantity = ev.target.value)}
          style={{ margin: "0", width: "75px" }}
          type="number"
          className="form-control"
          id="quantity"
          aria-describedby="emailHelp"
        ></input>
      </div>
      {!update ? (
        <button type="fc" className="btn btn-light" onClick={placeOrder}>
          <div className="btn-fc"></div>Place my order
        </button>
      ) : (
        <button type="fc" className="btn btn-light">
          <div className="btn-fc"></div>Update my order
        </button>
      )}
    </div>
  );
}

export { CatalogActions, GroupPageActions };
